import Vue from 'vue'
import VueRouter from 'vue-router'

Vue.use(VueRouter)

const routes = [
  {
    path: '/login',
    name: 'login',
    component: () => import('../views/login/MyLogin.vue'),
    meta:{
      index:50
    }
  },
  // 扶뒬못
  {
    path: '/home',
    name: 'home',
    component: () => import('../views/home/HomePage.vue'),
    meta: {
      showTabBar: true,
      index: 1
    }
  },
  {
    path:'/',
    name:'homePage',
    component: HomePage
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
