import Vue from 'vue'
import VueRouter from 'vue-router'
import MyMain from '@/views/Home/MyMain.vue'
import UserList from "@/views/Home/UserList.vue";
import AddUser from "@/views/Home/addUser.vue";
import MovieList from "@/views/Home/MovieList.vue";
import AddMovie from "@/views/Home/AddMovie.vue";

Vue.use(VueRouter)

const routes = [
  {
    path:'/',
    name:'login',
    component: Login
  },
  {
    path: '/home',
    name: 'main',
    component:MyMain,
    children:[
      {
        path:'/home/userList',
        component: UserList,
      },
      {
        path:'/home/addUser',
        component: AddUser
      },
      {
        path:'/home/addMovie',
        component: MovieList
      },
      {
        path:'/home/addMovie',
        component: AddMovie
      },

    ],

  },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
